#include <iostream>
using namespace std;
int main()
{
    int n;
    cout << "Enter a limit: ";
    cin >> n;

    

    // _ _ _ _ *
    // _ _ _ * *
    // _ _ * * *
    // _ * * * *
    // * * * * *

    for (int i = 1; i <= n; i++)
    {
        for (int j = n; j > i; j--)
            cout << "  ";
        for (int j = 1; j <= i; j++)
            cout << "* ";
        cout << endl;
    }

    

    // _ _ _ _ 1
    // _ _ _ 2 2
    // _ _ 3 3 3
    // _ 4 4 4 4
    // 5 5 5 5 5

    for (int i = 1; i <= n; i++)
    {
        for (int j = n; j > i; j--)
            cout << "  ";
        for (int j = 1; j <= i; j++)
            cout << i << " ";
        cout << endl;
    }

    

    // _ _ _ _ 1
    // _ _ _ 1 2
    // _ _ 1 2 3
    // _ 1 2 3 4
    // 1 2 3 4 5

    for (int i = 1; i <= n; i++)
    {
        for (int j = n; j > i; j--)
            cout << "  ";
        for (int j = 1; j <= i; j++)
            cout << j << " ";
        cout << endl;
    }

    

    // _ _ _ _ A
    // _ _ _ A B
    // _ _ A B C
    // _ A B C D
    // A B C D E

    for (int i = 1; i <= n; i++)
    {
        for (int j = n; j > i; j--)
            cout << "  ";
        for (int j = 1; j <= i; j++)
            cout << char('A' + (j - 1)) << " ";
        cout << endl;
    }

    

    // _ _ _ _ 1
    // _ _ _ 2 1
    // _ _ 3 2 1
    // _ 4 3 2 1
    // 5 4 3 2 1

    for (int i = 1; i <= n; i++)
    {
        for (int j = n; j > i; j--)
            cout << "  ";
        for (int j = i; j >= 1; j--)
            cout << j << " ";
        cout << endl;
    }

    

    //         *
    //       * * *
    //     * * * * *
    //   * * * * * * *
    // * * * * * * * * *

    for (int i = 1; i <= n; i++)
    {
        for (int j = 1; j <= (n - i); j++)
            cout << "  ";
        for (int j = 1; j <= (i * 2) - 1; j++)
            cout << "* ";
        cout << endl;
    }

    // * HOMEWORK:
    // Total Timepass

    return 0;
}