#include <iostream>
using namespace std;
int main()
{
    int n;
    cout << "Enter a limit: ";
    cin >> n;

    

    // _ _ _ _ 1
    // _ _ _ 1 2 1
    // _ _ 1 2 3 2 1
    // _ 1 2 3 4 3 2 1
    // 1 2 3 4 5 4 3 2 1

    for (int i = 1; i <= n; i++)
    {
        for (int j = 1; j <= n - i; j++)
            cout << "  ";
        for (int j = 1; j <= i; j++)
            cout << j << " ";
        for (int j = i - 1; j >= 1; j--)
            cout << j << " ";
        cout << endl;
    }

    

    // * * * * * * * * *
    //   * * * * * * *
    //     * * * * *
    //       * * *
    //         *

    for (int i = n; i >= 1; i--)
    {
        for (int j = 1; j <= n - i; j++)
            cout << "  ";
        for (int j = (2 * i) - 1; j >= 1; j--)
            cout << "* ";
        cout << endl;
    }

    

    // * * * * * * * * * *
    // * * * *     * * * *
    // * * *         * * *
    // * *             * *
    // *                 *
    // *                 *
    // * *             * *
    // * * *         * * *
    // * * * *     * * * *
    // * * * * * * * * * *

    for (int i = 1; i <= n; i++)
    {
        for (int j = 1; j <= (n - i) + 1; j++)
            cout << "* ";
        for (int j = 2; j < (2 * i); j++)
            cout << "  ";
        for (int j = 1; j <= (n - i) + 1; j++)
            cout << "* ";
        cout << endl;
    }
    for (int i = n; i >= 1; i--)
    {
        for (int j = 1; j <= (n - i) + 1; j++)
            cout << "* ";
        for (int j = 2; j < (2 * i); j++)
            cout << "  ";
        for (int j = 1; j <= (n - i) + 1; j++)
            cout << "* ";
        cout << endl;
    }

    

    // *                 *
    // * *             * *
    // * * *         * * *
    // * * * *     * * * *
    // * * * * * * * * * *
    // * * * *     * * * *
    // * * *         * * *
    // * *             * *
    // *                 *

    for (int i = 1; i <= n; i++)
    {
        for (int j = 1; j <= i; j++)
            cout << "* ";
        for (int j = 1; j <= (2 * (n - i)); j++)
            cout << "  ";
        for (int j = 1; j <= i; j++)
            cout << "* ";
        cout << endl;
    }
    for (int i = n - 1; i >= 1; i--)
    {
        for (int j = 1; j <= i; j++)
            cout << "* ";
        for (int j = 1; j <= (2 * (n - i)); j++)
            cout << "  ";
        for (int j = 1; j <= i; j++)
            cout << "* ";
        cout << endl;
    }

    

    //          *
    //        *   *
    //      *   *   *
    //    *   *   *   *
    //  *   *   *   *   *
    //  *   *   *   *   *
    //    *   *   *   *
    //      *   *   *
    //        *   *
    //          *

    for (int i = 1; i <= n; i++)
    {
        for (int j = 1; j <= (n - i) + 1; j++)
            cout << "  ";
        for (int j = 1; j <= i; j++)
            cout << " *  ";
        cout << endl;
    }
    for (int i = n; i >= 1; i--)
    {
        for (int j = 1; j <= (n - i) + 1; j++)
            cout << "  ";
        for (int j = 1; j <= i; j++)
            cout << " *  ";
        cout << endl;
    }

    return 0;
}

